var popup=document.querySelector(".modal-order"),iconCloseModal=document.querySelector(".modal-order__close"),overlay=document.querySelector(".overlay"),link=document.querySelector(".page-header__order");function hideModal(){popup.classList.remove("modal-show"),popup.classList.add("modal-hide"),overlay.classList.remove("overlay-show")}link.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-hide"),popup.classList.add("modal-show"),overlay.classList.add("overlay-show")}),iconCloseModal.addEventListener("click",function(e){e.preventDefault(),hideModal()}),overlay.addEventListener("click",function(e){e.preventDefault(),hideModal()}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),hideModal())});