let modalUser=document.querySelector(".modal-user"),btnModalUser=document.querySelector(".page-header__user"),closeUser=document.querySelector(".modal-user__close"),modalVideo=document.querySelector(".modal-video"),btnModalVideo=document.querySelector(".intro__video"),closeVideo=document.querySelector(".modal-video__close"),overlay=document.querySelector(".overlay");function workPopup(o,e,d,l){e.addEventListener("click",function(e){e.preventDefault(),o.classList.remove("modal-hide"),o.classList.add("modal-show"),l.classList.add("overlay-show")}),d.addEventListener("click",function(e){e.preventDefault(),hideModal(o,l)}),l.addEventListener("click",function(e){e.preventDefault(),hideModal(o,l)}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),hideModal(o,l))})}function hideModal(e,o){e.classList.remove("modal-show"),e.classList.add("modal-hide"),o.classList.remove("overlay-show")}workPopup(modalUser,btnModalUser,closeUser,overlay),workPopup(modalVideo,btnModalVideo,closeVideo,overlay);