jQuery(document).ready(function(t){const e=document.querySelector(".__select"),a=e.querySelector(".__select__title"),c=e.querySelectorAll(".__select__label");a.addEventListener("click",()=>{"active"===e.getAttribute("data-state")?e.setAttribute("data-state",""):e.setAttribute("data-state","active")});for(let t=0;t<c.length;t++)c[t].addEventListener("click",t=>{a.textContent=t.target.textContent,e.setAttribute("data-state","")});const r=document.querySelector(".reset");r.addEventListener("click",()=>{a.textContent=a.getAttribute("data-default")})});