var link=document.querySelector(".contact-us__button"),close=document.querySelector(".close"),iconClose=document.querySelector(".modal-contact__close"),popup=document.querySelector(".modal-contact"),overlay=document.querySelector(".overlay");function hide(){popup.classList.remove("modal-error"),popup.classList.remove("modal-show"),popup.classList.add("modal-hide"),overlay.classList.remove("overlay-show")}link.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-hide"),popup.classList.add("modal-show"),overlay.classList.add("overlay-show")}),close.addEventListener("click",function(e){e.preventDefault(),hide()}),iconClose.addEventListener("click",function(e){e.preventDefault(),hide()}),overlay.addEventListener("click",function(e){e.preventDefault(),hide()}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),hide())});